highlight('LineNr', nil, Color0, nil)
highlight('TabLine', Color8, nil, nil)
highlight('TabLineSel', Color6, Color7, nil)
highlight('TabLineFill', Color8, nil, nil)
highlight('TSPunctDelimiter', nil, Color6, nil)

